const WebSocket = require('ws');
const http = require('http');

// ایجاد یک سرور HTTP
const server = http.createServer();
const wss = new WebSocket.Server({ server });

// مدیریت اتصال کلاینت‌ها
wss.on('connection', (ws) => {
    console.log('یک کلاینت متصل شد.');

    // دریافت پیام از کلاینت
    ws.on('message', (message) => {
        console.log(`پیام دریافت شد: ${message}`);

        // ارسال داده‌های صوتی به همه کلاینت‌های دیگر
        wss.clients.forEach((client) => {
            if (client !== ws && client.readyState === WebSocket.OPEN) {
                client.send(message);
            }
        });
    });

    // مدیریت قطع اتصال
    ws.on('close', () => {
        console.log('یک کلاینت قطع شد.');
    });
});

// سرور را روی پورت 3000 اجرا کنید
server.listen(3000, () => {
    console.log('سرور WebSocket در پورت 3000 در حال اجرا است.');
});
